class Solution:
    def addOperators(self, num, target):
        res = []

        def backtrack(index, path, value, last):
            if index == len(num):
                if value == target:
                    res.append(path)
                return

            for i in range(index + 1, len(num) + 1):
                temp = num[index:i]
                if len(temp) > 1 and temp[0] == '0':
                    continue
                curr = int(temp)
                if index == 0:
                    backtrack(i, temp, curr, curr)
                else:
                    backtrack(i, path + '+' + temp, value + curr, curr)
                    backtrack(i, path + '-' + temp, value - curr, -curr)
                    backtrack(i, path + '*' + temp, value - last + last * curr, last * curr)

        backtrack(0, '', 0, 0)
        return res
