import random

class Solution:

    def __init__(self, nums):
        self.nums = list(nums)  # 原始陣列

    def reset(self):
        # 返回原始陣列
        return self.nums

    def shuffle(self):
        # 使用 Fisher-Yates 洗牌算法
        shuffled = list(self.nums)
        n = len(shuffled)
        for i in range(n):
            j = random.randint(i, n - 1)
            shuffled[i], shuffled[j] = shuffled[j], shuffled[i]
        return shuffled
