class Solution:
    def canIWin(self, maxChoosableInteger, desiredTotal):
        if desiredTotal <= 0:
            return True
        if (maxChoosableInteger * (maxChoosableInteger + 1)) // 2 < desiredTotal:
            return False

        memo = {}

        def can_win(used, total):
            if used in memo:
                return memo[used]

            for i in range(maxChoosableInteger):
                if not (used & (1 << i)):
                    if total <= i + 1 or not can_win(used | (1 << i), total - (i + 1)):
                        memo[used] = True
                        return True

            memo[used] = False
            return False

        return can_win(0, desiredTotal)
