class Solution:
    def findSubstringInWraproundString(self, p):
        # dp[i] 表示以字母 chr(i + ord('a')) 結尾的最大子串長度
        dp = [0] * 26
        k = 0  # 目前連續子串長度

        for i in range(len(p)):
            if i > 0 and (ord(p[i]) - ord(p[i-1])) % 26 == 1:
                k += 1
            else:
                k = 1
            dp[ord(p[i]) - ord('a')] = max(dp[ord(p[i]) - ord('a')], k)

        return sum(dp)
