import random

# 假設已經有一個 API rand7() 可用
def rand7():
    return random.randint(1,7)

class Solution:
    def rand10(self):
        while True:
            # 使用兩次 rand7 生成 1~49 的均勻分布
            num = (rand7() - 1) * 7 + rand7()  # 1~49
            if num <= 40:
                return 1 + (num - 1) % 10
