class Solution:
    def magicalString(self, n):
        if n == 0:
            return 0
        if n <= 3:
            return 1

        s = [1, 2, 2]  # 初始字符串
        head = 2  # 指向生成規則的位置
        num = 1  # 下一個要添加的數字
        while len(s) < n:
            s.extend([num] * s[head])
            num ^= 3  # 1 和 2 互換
            head += 1
        return s[:n].count(1)
