class Solution:
    def countArrangement(self, n):
        def backtrack(pos, remaining):
            if pos > n:
                return 1
            total = 0
            for x in list(remaining):
                if x % pos == 0 or pos % x == 0:
                    remaining.remove(x)
                    total += backtrack(pos + 1, remaining)
                    remaining.add(x)
            return total

        return backtrack(1, set(range(1, n + 1)))
