class Solution:
    def nearestPalindromic(self, n):
        length = len(n)
        num = int(n)
        candidates = set()

        # 特殊情況
        candidates.add(str(10 ** length + 1))
        candidates.add(str(10 ** (length - 1) - 1))

        prefix = int(n[:(length + 1) // 2])
        for i in [-1, 0, 1]:
            new_prefix = str(prefix + i)
            if length % 2 == 0:
                palin = new_prefix + new_prefix[::-1]
            else:
                palin = new_prefix + new_prefix[:-1][::-1]
            candidates.add(palin)

        candidates.discard(n)

        res = None
        for cand in candidates:
            if res is None or abs(int(cand) - num) < abs(int(res) - num) or (abs(int(cand) - num) == abs(int(res) - num) and int(cand) < int(res)):
                res = cand

        return res
