class Solution:
    def findIntegers(self, n):
        # dp[i] 表示長度為 i 的二進制不含連續1的數量
        dp = [0] * 32
        dp[0] = 1
        dp[1] = 2
        for i in range(2, 32):
            dp[i] = dp[i-1] + dp[i-2]

        ans = 0
        prev_bit = 0
        for i in reversed(range(31)):
            if n & (1 << i):
                ans += dp[i]
                if prev_bit:
                    return ans
                prev_bit = 1
            else:
                prev_bit = 0
        return ans + 1
