class Solution(object):
    def solveEquation(self, equation):
        """
        :type equation: str
        :rtype: str
        """
        def parse_side(s):
            x_coeff, const = 0, 0
            i, n, sign = 0, len(s), 1
            while i < n:
                if s[i] == '+':
                    sign = 1
                    i += 1
                elif s[i] == '-':
                    sign = -1
                    i += 1
                else:
                    j = i
                    while j < n and s[j] not in '+-':
                        j += 1
                    token = s[i:j]
                    if 'x' in token:
                        coeff = token[:-1]
                        if coeff == '' or coeff == '+':
                            coeff = 1
                        elif coeff == '-':
                            coeff = -1
                        else:
                            coeff = int(coeff)
                        x_coeff += sign * coeff
                    else:
                        const += sign * int(token)
                    i = j
            return x_coeff, const

        left, right = equation.split('=')
        left_x, left_const = parse_side(left)
        right_x, right_const = parse_side(right)

        x_total = left_x - right_x
        const_total = right_const - left_const

        if x_total == 0:
            if const_total == 0:
                return "Infinite solutions"
            else:
                return "No solution"
        else:
            return "x=" + str(const_total // x_total)
