class Solution:
    def replaceWords(self, dictionary, sentence):
        trie = {}
        END = True

        # 建立 Trie
        for root in dictionary:
            node = trie
            for char in root:
                node = node.setdefault(char, {})
            node[END] = root

        def replace(word):
            node = trie
            for char in word:
                if char not in node:
                    break
                node = node[char]
                if END in node:
                    return node[END]
            return word

        return ' '.join(map(replace, sentence.split()))
