class Solution:
    def canPartitionKSubsets(self, nums, k):
        total = sum(nums)
        if total % k != 0:
            return False
        target = total // k
        n = len(nums)
        nums.sort(reverse=True)
        memo = {}

        def dfs(used, curr_sum, k_remaining):
            if k_remaining == 0:
                return True
            if (used, curr_sum, k_remaining) in memo:
                return memo[(used, curr_sum, k_remaining)]
            for i in range(n):
                if not (used >> i) & 1 and curr_sum + nums[i] <= target:
                    if dfs(used | (1 << i), (curr_sum + nums[i]) % target, k_remaining - ((curr_sum + nums[i]) // target)):
                        memo[(used, curr_sum, k_remaining)] = True
                        return True
            memo[(used, curr_sum, k_remaining)] = False
            return False

        return dfs(0, 0, k)
