class Solution:
    def maxProfit(self, prices, fee):
        n = len(prices)
        if n == 0:
            return 0
        hold = -prices[0]
        cash = 0
        for price in prices[1:]:
            cash = max(cash, hold + price - fee)
            hold = max(hold, cash - price)
        return cash
