class Solution(object):
    def uniqueLetterString(self, s):
        last = {}
        prev = {}
        res = 0
        for i, c in enumerate(s):
            if c not in prev:
                prev[c] = -1
            if c not in last:
                last[c] = -1
            res += (i - prev[c]) * (prev[c] - last[c])
            last[c], prev[c] = prev[c], i
        n = len(s)
        for c in prev:
            res += (n - prev[c]) * (prev[c] - last[c])
        return res
