class Solution(object):
    def new21Game(self, N, K, W):
        """
        :type N: int
        :type K: int
        :type W: int
        :rtype: float
        """
        if K == 0 or N >= K + W:
            return 1.0

        dp = [0.0] * (N + 1)
        for i in range(K, N + 1):
            dp[i] = 1.0

        window_sum = min(N - K + 1, W)
        for i in range(K - 1, -1, -1):
            dp[i] = window_sum / float(W)
            window_sum = dp[i] + window_sum
            if i + W <= N:
                window_sum -= dp[i + W]

        return dp[0]
