class Solution(object):
    def nthMagicalNumber(self, N, A, B):
        """
        :type N: int
        :type A: int
        :type B: int
        :rtype: int
        """
        MOD = 10**9 + 7

        def gcd(x, y):
            while y:
                x, y = y, x % y
            return x

        lcm = A * B // gcd(A, B)

        left, right = 1, N * min(A, B)
        while left < right:
            mid = (left + right) // 2
            count = mid // A + mid // B - mid // lcm
            if count < N:
                left = mid + 1
            else:
                right = mid

        return left % MOD
