class Solution:
    def maxNumber(self, nums1, nums2, k):
        def pick_max(nums, t):
            drop = len(nums) - t
            stack = []
            for num in nums:
                while drop and stack and stack[-1] < num:
                    stack.pop()
                    drop -= 1
                stack.append(num)
            return stack[:t]

        def merge(a, b):
            res = []
            while a or b:
                if a > b:
                    res.append(a.pop(0))
                else:
                    res.append(b.pop(0))
            return res

        max_res = []
        for i in range(max(0, k - len(nums2)), min(k, len(nums1)) + 1):
            a = pick_max(nums1, i)
            b = pick_max(nums2, k - i)
            max_res = max(max_res, merge(a[:], b[:]))
        return max_res
