import heapq

class Solution:
    def kthSmallest(self, matrix, k):
        n = len(matrix)
        # 使用最小堆儲存 (數值, 行, 列)
        heap = [(matrix[i][0], i, 0) for i in range(n)]
        heapq.heapify(heap)

        # 提取 k-1 次最小元素
        for _ in range(k - 1):
            val, r, c = heapq.heappop(heap)
            if c + 1 < n:
                heapq.heappush(heap, (matrix[r][c + 1], r, c + 1))

        return heapq.heappop(heap)[0]
