class Solution:
    def findMaxForm(self, strs, m, n):
        # dp[i][j] 表示使用 i 個 0 和 j 個 1 能形成的最大字串數
        dp = [[0] * (n + 1) for _ in range(m + 1)]

        for s in strs:
            zeros = s.count('0')
            ones = s.count('1')
            # 逆序遍歷，避免重複使用同一個字串
            for i in range(m, zeros - 1, -1):
                for j in range(n, ones - 1, -1):
                    dp[i][j] = max(dp[i][j], dp[i - zeros][j - ones] + 1)
        return dp[m][n]
