class Solution:
    def smallestGoodBase(self, n):
        n = int(n)
        max_m = n.bit_length()  # 最大位數

        for m in range(max_m, 1, -1):
            left, right = 2, n - 1
            while left <= right:
                k = (left + right) // 2
                # 計算 1 + k + k^2 + ... + k^(m-1)
                s = (k**m - 1) // (k - 1)
                if s == n:
                    return str(k)
                elif s < n:
                    left = k + 1
                else:
                    right = k - 1
        return str(n - 1)
