class Solution:
    def maxSumOfThreeSubarrays(self, nums, k):
        n = len(nums)
        w = [0] * (n - k + 1)
        s = sum(nums[:k])
        w[0] = s
        for i in range(1, n - k + 1):
            s += nums[i + k - 1] - nums[i - 1]
            w[i] = s

        left = [0] * len(w)
        best = 0
        for i in range(len(w)):
            if w[i] > w[best]:
                best = i
            left[i] = best

        right = [0] * len(w)
        best = len(w) - 1
        for i in range(len(w) - 1, -1, -1):
            if w[i] >= w[best]:
                best = i
            right[i] = best

        res = None
        for j in range(k, len(w) - k):
            i = left[j - k]
            l = right[j + k]
            total = w[i] + w[j] + w[l]
            if res is None or total > res[0]:
                res = (total, [i, j, l])
        return res[1]
