import bisect

class RangeModule:
    def __init__(self):
        self.ranges = []

    def addRange(self, left, right):
        new_ranges = []
        placed = False
        for l, r in self.ranges:
            if r < left:
                new_ranges.append([l, r])
            elif right < l:
                if not placed:
                    new_ranges.append([left, right])
                    placed = True
                new_ranges.append([l, r])
            else:
                left = min(left, l)
                right = max(right, r)
        if not placed:
            new_ranges.append([left, right])
        self.ranges = new_ranges

    def queryRange(self, left, right):
        i = bisect.bisect_right(self.ranges, [left, float('inf')])
        if i == 0:
            return False
        return self.ranges[i-1][0] <= left and right <= self.ranges[i-1][1]

    def removeRange(self, left, right):
        new_ranges = []
        for l, r in self.ranges:
            if r <= left or l >= right:
                new_ranges.append([l, r])
            else:
                if l < left:
                    new_ranges.append([l, left])
                if r > right:
                    new_ranges.append([right, r])
        self.ranges = new_ranges
