class Solution:
    def intersectionSizeTwo(self, intervals):
        intervals.sort(key=lambda x: (x[1], -x[0]))
        res = []
        last, second_last = -1, -1
        for start, end in intervals:
            count = 0
            if start <= second_last <= end:
                count += 1
            if start <= last <= end:
                count += 1
            if count >= 2:
                continue
            elif count == 1:
                res.append(end)
                second_last, last = last, end
            else:
                res.extend([end-1, end])
                second_last, last = end-1, end
        return len(res)
