class Solution:
    def numSubarrayBoundedMax(self, nums, left, right):
        def count(bound):
            ans = cur = 0
            for x in nums:
                if x <= bound:
                    cur += 1
                    ans += cur
                else:
                    cur = 0
            return ans
        return count(right) - count(left - 1)
