class Solution(object):
    def bestRotation(self, A):
        n = len(A)
        change = [0] * n

        for i, a in enumerate(A):
            lose_start = (i - a + 1 + n) % n
            lose_end = (i + 1) % n
            change[lose_start] -= 1
            change[lose_end] += 1
            if lose_start > lose_end:
                change[0] -= 1

        max_score = -n
        score = 0
        best_k = 0

        for k in xrange(n):
            score += change[k]
            if score > max_score:
                max_score = score
                best_k = k
        return best_k
