class Solution(object):
    def lenLongestFibSubseq(self, A):
        s = set(A)
        n = len(A)
        longest = {}
        ans = 0

        for i in range(n):
            for j in range(i):
                x, y = A[j], A[i]
                if x + y in s:
                    longest[y, x + y] = longest.get((x, y), 2) + 1
                    ans = max(ans, longest[y, x + y])
        return ans
